namespace Villains.Library.Tests.Services;

public class Base64ServiceTests
{
    [Fact]
    public void GetStreamFromBase64String_StringStartsWithData_Success()
    {
        // arrange
        var base64String =
            @"data:image/jpeg;base64,";

        // act
        var result = Base64Service.GetBytesFromBase64String(base64String);

        // result
        result.IsSuccess.Should().BeTrue();
        result.Value.Length.Should().BeGreaterThan(0);
    }
}